import{_ as t,c as r,o as n,ae as a}from"./chunks/framework.B8KKhRA6.js";const c=JSON.parse('{"title":"Datenschutzerklärung für app.selbsttest.org","description":"","frontmatter":{},"headers":[],"relativePath":"privacy-app.md","filePath":"privacy-app.md"}'),i={name:"privacy-app.md"};function s(u,e,l,h,d,g){return n(),r("div",null,e[0]||(e[0]=[a('<h1 id="datenschutzerklarung-fur-app-selbsttest-org" tabindex="-1">Datenschutzerklärung für app.selbsttest.org <a class="header-anchor" href="#datenschutzerklarung-fur-app-selbsttest-org" aria-label="Permalink to &quot;Datenschutzerklärung für app.selbsttest.org&quot;">​</a></h1><h2 id="_1-verantwortlicher" tabindex="-1">1. Verantwortlicher <a class="header-anchor" href="#_1-verantwortlicher" aria-label="Permalink to &quot;1. Verantwortlicher&quot;">​</a></h2><p><strong>Dr. Mark Heckmann</strong><br> Steinhäuser Str. 7<br> 28195 Bremen<br> Deutschland<br> E-Mail: <a href="mailto:kontakt@selbsttest.org" target="_blank" rel="noreferrer">kontakt@selbsttest.org</a></p><h2 id="_2-datenschutzbeauftragter" tabindex="-1">2. Datenschutzbeauftragter <a class="header-anchor" href="#_2-datenschutzbeauftragter" aria-label="Permalink to &quot;2. Datenschutzbeauftragter&quot;">​</a></h2><p><strong>Dr. Mark Heckmann</strong><br> Anschrift, siehe oben</p><h2 id="_3-zwecke-und-rechtsgrundlagen-der-verarbeitung" tabindex="-1">3. Zwecke und Rechtsgrundlagen der Verarbeitung <a class="header-anchor" href="#_3-zwecke-und-rechtsgrundlagen-der-verarbeitung" aria-label="Permalink to &quot;3. Zwecke und Rechtsgrundlagen der Verarbeitung&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Verarbeitungsvorgang</th><th>Zwecke</th><th>Rechtsgrundlage Art. 6 DSGVO</th></tr></thead><tbody><tr><td>Aufruf der Website / Server-Log-Files</td><td>Stabilität, Sicherheit, Fehleranalyse</td><td>lit. f – berechtigtes Interesse</td></tr><tr><td>Registrierung &amp; Nutzerkonto</td><td>Vertragserfüllung, Support</td><td>lit. b</td></tr><tr><td>14-tägige Testphase</td><td>Durchführung vorvertraglicher Maßnahmen</td><td>lit. b</td></tr><tr><td>Vertragsabwicklung, Rechnungsstellung</td><td>Entgeltabrechnung, Buchhaltung</td><td>lit. b, lit. c (GoBD)</td></tr><tr><td>Durchführung der Befragungen</td><td>Auftragsverarbeitung im Auftrag des Nutzers</td><td>Art. 28 i. V. m. lit. b</td></tr><tr><td>Kontakt per E-Mail</td><td>Beantwortung von Anfragen</td><td>lit. b (vertraglich) bzw. lit. f</td></tr><tr><td>Hosting bei Hetzner</td><td>Infrastruktur-Betrieb</td><td>lit. f</td></tr></tbody></table><p>Cookies &amp; Tracker: Es werden lediglich technisch notwendige Session-Cookies eingesetzt (§ 25 Abs. 2 Nr. 2 TTDSG). Analyse-Tools externer Dritter werden nicht verwendet.</p><h2 id="_4-empfanger-kategorien-von-empfangern" tabindex="-1">4. Empfänger / Kategorien von Empfängern <a class="header-anchor" href="#_4-empfanger-kategorien-von-empfangern" aria-label="Permalink to &quot;4. Empfänger / Kategorien von Empfängern&quot;">​</a></h2><ul><li>Hetzner Online GmbH (Hosting, Auftragsverarbeitung)</li><li>Steuer- und Wirtschaftsprüfer, sofern gesetzlich erforderlich</li><li>Zahlungsdienstleister/Banken im Rahmen der Rechnungsabwicklung</li></ul><p>Eine Übermittlung in Drittländer findet nicht statt.</p><h2 id="_5-dauer-der-speicherung" tabindex="-1">5. Dauer der Speicherung <a class="header-anchor" href="#_5-dauer-der-speicherung" aria-label="Permalink to &quot;5. Dauer der Speicherung&quot;">​</a></h2><ul><li>Nutzerkonto-Daten: Bis zur Löschung des Kontos; darüber hinaus solange gesetzliche Aufbewahrungspflichten (HGB, AO) bestehen.</li><li>Server-Logs: 7 Tage, danach anonymisiert.</li><li>Befragungsdaten: Gemäß Einstellung des jeweiligen Nutzers; spätestens 30 Tage nach Kontolöschung.</li></ul><h2 id="_6-betroffenenrechte" tabindex="-1">6. Betroffenenrechte <a class="header-anchor" href="#_6-betroffenenrechte" aria-label="Permalink to &quot;6. Betroffenenrechte&quot;">​</a></h2><p>Sie haben jederzeit das Recht auf Auskunft (Art. 15), Berichtigung (Art. 16), Löschung (Art. 17), Einschränkung (Art. 18), Datenübertragbarkeit (Art. 20) sowie Widerspruch (Art. 21). Zur Ausübung genügt eine Nachricht an <a href="mailto:datenschutz@selbsttest.org" target="_blank" rel="noreferrer">datenschutz@selbsttest.org</a>. Zudem besteht ein Beschwerderecht bei der zuständigen Aufsichtsbehörde (Berliner Beauftragte für Datenschutz und Informationsfreiheit).</p><h2 id="_7-sicherheit-der-verarbeitung" tabindex="-1">7. Sicherheit der Verarbeitung <a class="header-anchor" href="#_7-sicherheit-der-verarbeitung" aria-label="Permalink to &quot;7. Sicherheit der Verarbeitung&quot;">​</a></h2><ul><li><p>TLS-Verschlüsselung (HTTPS)</p></li><li><p>Verschlüsselung von Passwörtern (bcrypt)</p></li><li><p>Role-Based-Access-Control, Zwei-Faktor-Authentifizierung für Admins</p><p>Tägliche Backups, Aufbewahrung 14 Tage in isoliertem Rechenzentrum</p></li></ul><h2 id="_8-verpflichtung-zur-bereitstellung" tabindex="-1">8. Verpflichtung zur Bereitstellung <a class="header-anchor" href="#_8-verpflichtung-zur-bereitstellung" aria-label="Permalink to &quot;8. Verpflichtung zur Bereitstellung&quot;">​</a></h2><p>Die Angabe von E-Mail-Adresse, Namen und Anschrift ist für Vertragsschluss erforderlich. Ohne diese Angaben ist eine Registrierung nicht möglich.</p><h2 id="_9-automatisierte-entscheidungsfindung-profiling" tabindex="-1">9. Automatisierte Entscheidungsfindung / Profiling <a class="header-anchor" href="#_9-automatisierte-entscheidungsfindung-profiling" aria-label="Permalink to &quot;9. Automatisierte Entscheidungsfindung / Profiling&quot;">​</a></h2><p>Findet nicht statt.</p><h2 id="_10-anderung-der-datenschutzerklarung" tabindex="-1">10. Änderung der Datenschutzerklärung <a class="header-anchor" href="#_10-anderung-der-datenschutzerklarung" aria-label="Permalink to &quot;10. Änderung der Datenschutzerklärung&quot;">​</a></h2><p>Diese Erklärung wird bei Änderungen der Datenverarbeitung oder Rechtslage angepasst. Über wesentliche Änderungen werden Nutzer per E-Mail informiert.</p>',23)]))}const b=t(i,[["render",s]]);export{c as __pageData,b as default};
